import React from 'react'
import {useState} from 'react';

export const AddTodo = (props) => {

    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");

    const submit = () => {
        
    }

    return (
        <div className="container">
            <h2 className="text-center">Add a Todo</h2>
            <form onSubmit={submit}>
                <div className="mb-3">
                    <label for="title" className="form-label">Todo Title</label>
                    <input type="text" value={title} onChange={(e) => {
                        setTitle.apply(e.target.value)
                    }} className="form-control" id="tite" aria-describedby="emailHelp" />
            
                </div>
                <div className="mb-3">
                    <label for="desc" className="form-label">Todo Description</label>
                    <input type="text" value={desc} onChange={(e) => {
                        setDesc.apply(e.target.value)
                    }} className="form-control" id="desc" />
                </div>
                <button type="submit" className="btn btn-sm btn-success">Add Todo</button>
            </form>
        </div>
    )
}
